const choice = $json.choices?.;
const content = choice?.message?.content || '{}';
let obj;
try { obj = JSON.parse(content); } catch(e) { obj = { title_translate: 'Error de parseo', summary_translate: 'Salida no válida' }; }
return [{ json: { url: $json.url, title: $json.title, ...obj } }];

// Entrada esperada: item.json.data = markdown de r.jina.ai, item.json.url (fallback)
const md = ($json.data || '').toString();

// 1) Título: preferir "Title: …"; fallback a H1 setext (línea + =====) y luego a H1 ATX (# …)
let title = null;
const mTitle = md.match(/^\s*Title:\s*(.+)\s*$/m);
if (mTitle) title = mTitle[1].trim();
if (!title) {
  const mSetext = md.match(/^\s*([^\n]+)\n=+\s*$/m); // H1 setext
  if (mSetext) title = mSetext[1].trim();
}
if (!title) {
  const mAtx = md.match(/^\s*#\s+(.+?)\s*$/m); // H1 ATX
  if (mAtx) title = mAtx[1].trim();
}
if (!title) title = 'Sin título';

// 2) URL: preferir "URL Source: …"; fallback al campo url del item si existe
let url = ($json.url || '').toString().trim();
const mUrl = md.match(/^\s*URL Source:\s*(\S+)\s*$/m);
if (mUrl) url = mUrl[1].trim();

// 3) Contenido: si existe el marcador "Markdown Content:", cortar desde ahí;
//    si no, eliminar cabeceras (Title/URL/Published) y también quitar el H1 setext inicial
let content;
const marker = 'Markdown Content:';
const idx = md.indexOf(marker);
if (idx !== -1) {
  content = md.slice(idx + marker.length).trim();
} else {
  content = md
    .replace(/^\s*Title:.*$/m, '')
    .replace(/^\s*URL Source:.*$/m, '')
    .replace(/^\s*Published Time:.*$/m, '')
    .trim();
}
// Quitar H1 setext al inicio si quedara
content = content.replace(/^\s*([^\n]+)\n=+\s*\n?/, '').trim();

// Normalizar y limitar
content = content.replace(/[ \t]+\n/g, '\n').replace(/\n{3,}/g, '\n\n').trim();
if (content.length > 8000) content = content.slice(0, 8000) + '...';

// 4) Heurística opcional de paywall (puedes omitirla si no te interesa)
const paywalled = /member-only|This story is for members|subscribe to.*Medium/i.test(md);

// Salida con las claves pedidas
return [
  {
    json: { title, url, content, paywalled }
  }
];
