// 1) Leer el string JSON devuelto por Chat Completions
const raw = $json?.choices?.[0]?.message?.content ?? "{}";
// 2) Parsear con tolerancia a errores
let data;
try {
  data = JSON.parse(raw);
} catch {
  data = {
    title_translate: "Salida no válida",
    summary_translate: "El modelo no devolvió JSON parseable."
  };
}
// 3) Recuperar título y URL del nodo anterior
const prev = $('Parse Reader Markdown').first().json;
const title = prev?.title ?? "";
const url = prev?.url ?? "";
// 4) Salida final
return [
  {
    json: {
      title,
      title_translate: data.title_translate ?? "",
      summary_translate: data.summary_translate ?? "",
      url
    }
  }
];

// Procesa todos los items de entrada y devuelve uno por cada URL
const out = items.map((item) => {
  const md = (item.json?.data || '').toString();

  // 1) Título
  let title = null;
  let m = md.match(/^\s*Title:\s*(.+)\s*$/m);
  if (m) title = m[1].trim();
  if (!title) {
    m = md.match(/^\s*([^\n]+)\n=+\s*$/m); // H1 setext
    if (m) title = m[1].trim();
  }
  if (!title) {
    m = md.match(/^\s*#\s+(.+?)\s*$/m); // H1 ATX
    if (m) title = m[1].trim();
  }
  if (!title) title = 'Sin título';

  // 2) URL - Primero intenta extraer de enlaces Sign in, si no, usa URL Source
  let url = (item.json?.url || '').toString().trim();

  // Buscar enlaces de Sign in con redirect
  const signInMatch = md.match(/\[Sign in\]\(http:\/\/medium\.com\/m\/signin\?operation=login&redirect=([^)]+)\)/);
  if (signInMatch) {
    // Decodificar la URL del parámetro redirect
    const encodedUrl = signInMatch[1];
    url = decodeURIComponent(encodedUrl);

    // Limpiar parámetros después de &source=post_page
    const sourceIndex = url.indexOf('&source=post_page');
    if (sourceIndex !== -1) {
      url = url.substring(0, sourceIndex);
    }
  } else {
    // Si no hay Sign in link, usar URL Source como fallback
    m = md.match(/^\s*URL Source:\s*(\S+)\s*$/m);
    if (m) url = m[1].trim();
  }

  // 3) Cuerpo
  const marker = 'Markdown Content:';
  const idx = md.indexOf(marker);
  let content = (idx !== -1)
    ? md.slice(idx + marker.length).trim()
    : md
        .replace(/^\s*Title:.*$/m, '')
        .replace(/^\s*URL Source:.*$/m, '')
        .replace(/^\s*Published Time:.*$/m, '')
        .trim();

  // Limpiar posibles H1 setext inicial y normalizar saltos
  content = content.replace(/^\s*([^\n]+)\n=+\s*\n?/, '').trim();
  content = content.replace(/[ \t]+\n/g, '\n').replace(/\n{3,}/g, '\n\n').trim();
  if (content.length > 8000) content = content.slice(0, 8000) + '...';

  // 4) Heurística de paywall
  const paywalled = /member-only|This story is for members|subscribe to.*Medium/i.test(md);

  return { json: { title, url, content, paywalled } };
});

return out;
