const seen = new Set();
const filtered = [];
for (const item of items) {
  const url = item.json.linkUrl;
  if (!url) continue;
  if (url.includes('unsubscribe') || url.includes('help') || url.includes('privacy')) continue;
  if (!seen.has(url)) {
    seen.add(url);
    filtered.push({ json: { url } });
  }
}
return filtered;

const date = new Date().toISOString().split('T')[0];
let md = `# Medium Daily Digest — ${date}\n\n`;
for (const item of items) {
  const t = item.json.title || 'Sin título';
  const s = item.json.text || item.json.summary || 'Sin resumen';
  const u = item.json.url || '';
  md += `## ${t}\n\n${s}\n\nLink: ${u}\n\n`;
}
return [{ json: { content: md } }];
