// Extract article links from Medium Daily Digest email HTML
const emailData = $input.all();

if (!emailData || emailData.length === 0) {
  return [];
}

const results = [];

for (const email of emailData) {
  const htmlContent = email.json.payload?.parts?.find(part => part.mimeType === 'text/html')?.body?.data;

  if (!htmlContent) {
    console.log('No HTML content found in email');
    continue;
  }

  // Decode base64 HTML content
  const decodedHtml = Buffer.from(htmlContent, 'base64').toString('utf-8');

  // Extract Medium article links using regex
  // Look for links that contain medium.com and appear to be article URLs
  const linkRegex = /https:\/\/[^\s"'<>]*medium\.com\/[^\s"'<>]*[a-f0-9]{12}/gi;
  const matches = decodedHtml.match(linkRegex) || [];

  // Remove duplicates and filter valid article URLs
  const uniqueLinks = [...new Set(matches)]
    .filter(link => {
      // Filter out non-article URLs (like profile pages, topic pages, etc.)
      return !link.includes('/tag/') && 
             !link.includes('/topics/') && 
             !link.includes('/@') && 
             link.includes('-');
    })
    .slice(0, 10); // Limit to 10 articles to avoid rate limiting

  console.log(`Found ${uniqueLinks.length} unique Medium article links`);

  // Create separate items for each link
  uniqueLinks.forEach((link, index) => {
    results.push({
      json: {
        articleUrl: link,
        emailId: email.json.id,
        emailSubject: email.json.payload?.headers?.find(h => h.name === 'Subject')?.value || 'Medium Daily Digest',
        emailDate: email.json.payload?.headers?.find(h => h.name === 'Date')?.value || new Date().toISOString(),
        articleIndex: index + 1
      }
    });
  });
}

return results;

// Extract article title and content, then create summary
const items = $input.all();
const results = [];

for (const item of items) {
  const html = item.json.data || '';
  const articleUrl = item.json.articleUrl;

  if (!html) {
    results.push({
      json: {
        ...item.json,
        title: 'Unable to fetch article',
        summary: 'Could not retrieve article content.',
        error: 'No HTML content received'
      }
    });
    continue;
  }

  try {
    // Extract title - try multiple selectors
    let title = '';
    const titleMatches = [
      html.match(/<title[^>]*>([^<]+)<\/title>/i),
      html.match(/<h1[^>]*>([^<]+)<\/h1>/i),
      html.match(/<meta[^>]*property=["']og:title["'][^>]*content=["']([^"']+)["']/i),
      html.match(/<meta[^>]*name=["']title["'][^>]*content=["']([^"']+)["']/i)
    ];

    for (const match of titleMatches) {
      if (match && match[1]) {
        title = match[1].trim();
        // Clean up title
        title = title.replace(/\s*\|\s*Medium$/, '').replace(/\s*â€”\s*Medium$/, '').trim();
        break;
      }
    }

    if (!title) {
      title = 'Medium Article';
    }

    // Extract article content - look for common Medium selectors
    let content = '';
    const contentMatches = [
      html.match(/<article[^>]*>([\s\S]*?)<\/article>/i),
      html.match(/<div[^>]*class=["'][^"']*article[^"']*["'][^>]*>([\s\S]*?)<\/div>/i),
      html.match(/<main[^>]*>([\s\S]*?)<\/main>/i)
    ];

    for (const match of contentMatches) {
      if (match && match[1]) {
        content = match[1];
        break;
      }
    }

    if (!content) {
      // Fallback: extract all paragraph content
      const paragraphs = html.match(/<p[^>]*>([\s\S]*?)<\/p>/gi) || [];
      content = paragraphs.slice(0, 10).join(' '); // First 10 paragraphs
    }

    // Clean HTML tags and extract text
    const cleanText = content
      .replace(/<script[\s\S]*?<\/script>/gi, '')
      .replace(/<style[\s\S]*?<\/style>/gi, '')
      .replace(/<[^>]+>/g, ' ')
      .replace(/\s+/g, ' ')
      .trim();

    // Create summary (first 2-3 sentences or ~200 characters)
    let summary = '';
    if (cleanText) {
      const sentences = cleanText.match(/[^.!?]+[.!?]+/g) || [];
      if (sentences.length >= 2) {
        summary = sentences.slice(0, 3).join(' ').trim();
      } else {
        summary = cleanText.substring(0, 200).trim();
        if (summary.length === 200 && cleanText.length > 200) {
          summary += '...';
        }
      }
    }

    if (!summary) {
      summary = 'Unable to extract article summary from content.';
    }

    results.push({
      json: {
        ...item.json,
        title: title,
        summary: summary,
        wordCount: cleanText.split(' ').length,
        extractedAt: new Date().toISOString()
      }
    });

  } catch (error) {
    console.error('Error processing article:', error);
    results.push({
      json: {
        ...item.json,
        title: 'Processing Error',
        summary: 'An error occurred while processing this article.',
        error: error.message
      }
    });
  }
}

return results;

// Generate markdown file with all articles
const items = $input.all();

if (!items || items.length === 0) {
  return [{
    json: {
      fileName: `medium_digest_${new Date().toISOString().split('T')[0]}.md`,
      content: '# Medium Daily Digest\n\nNo articles found in today\'s digest.',
      articleCount: 0
    }
  }];
}

const articles = items[0].json.articles || [];
const today = new Date();
const dateStr = today.toISOString().split('T')[0];
const formattedDate = today.toLocaleDateString('en-US', { 
  weekday: 'long', 
  year: 'numeric', 
  month: 'long', 
  day: 'numeric' 
});

// Create markdown content
let markdown = `# Medium Daily Digest - ${formattedDate}\n\n`;
markdown += `*Generated on ${today.toISOString()}*\n\n`;
markdown += `Found ${articles.length} articles in today's digest:\n\n`;
markdown += '---\n\n';

// Add each article
articles.forEach((article, index) => {
  markdown += `## ${index + 1}. ${article.title}\n\n`;
  markdown += `**Summary:** ${article.summary}\n\n`;
  markdown += `**Link:** [Read on Medium](${article.articleUrl})\n\n`;

  if (article.wordCount) {
    markdown += `*Estimated reading: ~${Math.ceil(article.wordCount / 200)} minutes*\n\n`;
  }

  markdown += '---\n\n';
});

// Add footer
markdown += `\n*This digest was automatically generated by n8n workflow.*\n`;
markdown += `*Total articles processed: ${articles.length}*\n`;

const fileName = `medium_digest_${dateStr}.md`;

return [{
  json: {
    fileName: fileName,
    content: markdown,
    articleCount: articles.length,
    generatedAt: today.toISOString(),
    articles: articles
  }
}];
